import React, { Component } from "react";
import { timeout } from "q";

class Counter extends Component {
  state = {
    count: 0,
    tags: ["tag1", "tag2", "tag3"]
  };

  styles = {
    fontSize: 15,
    fontWeight: "bold"
  };
  render() {
    return (
      <div>
        <ul>
          {this.state.tags.map(tag => (
            <li key={tag}>{tag}</li>
          ))}
          {/* mapping string to JSX expression, similar to lazy coding  */}
        </ul>
      </div>
    ); //JSX component
